import transportStatusMap from './transportStatusMap'


let index = 0

const create = (factoryId, constructionId, transportType) => {
  const id = index++

  return {
    [id]: { id, factoryId, constructionId, transportType, status: transportStatusMap.READY },
  }
}

export default async () => {
  index = 0

  return {
    ...create(101, 208, 307),
    ...create(101, 204, 307),
    ...create(101, 205, 307),
    ...create(102, 201, 303),
    ...create(101, 204, 303),
    ...create(102, 203, 303),
    ...create(102, 204, 303),
    ...create(101, 205, 303),
  }
}